# Project Create when lead move 'lead (id=1)' to 'Initial Appointments (id=2)'
for rec in records:
    data = env['project.task'].search([('x_studio_opportunity', '=', rec.id)])
    if not data:
        for product in rec.x_studio_products:
            # Template Project copy that project
            proj_id = env.ref('__export__.project_project_28_9e442eef')
            project_id = env['project.project'].browse(proj_id.id).copy({'name': str(rec.id) + '-' + rec.name + '-' + product.name})
            project_id.task_ids.write({'x_studio_opportunity': rec.id})
